import React, {useState, useEffect} from 'react';
import dynamic from 'next/dynamic'
const Reactpip = dynamic( () => import('react-picture-in-picture'), {ssr: false}) 

const Video = ({}) => {
  // const scroll = useScroll()

  const [state, setState] = useState({
    scrollY: 0
  })

  const {scrollY} = state

  useEffect( () => {
    window.addEventListener('scroll', handleScroll);
  }, [scroll])

  const handleScroll = () => {
    setState({...state, scrollY: window.scrollY});
  }

  return (
    <div className="video">
      {scrollY}
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className="video-wrap">
        <div className="video-player">
          <iframe src="/media/video.mp4" frameborder="0" gesture="media" allowfullscreen></iframe>
        </div>
        {scrollY}
      </div>
    </div>
  )
}

export default Video
