import React, {useState, useEffect, useRef} from 'react'
import dynamic from 'next/dynamic'
import YouTube from 'react-youtube';

// const YouTubePlayer = dynamic( () => import('youtube-player'), {ssr: false}) ;

const Video = ({}) => {

  const [state, setState] = useState({
    videoWrap: false,
    videoSticky: true
  })

  const {videoWrap, videoSticky} = state

  const vidRef = useRef(null)

  const opts = {
    height: '390',
    width: '640',
    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
    },
  };

  const videoOnReady = (event) => {
    // access to player in all event handlers via event.target
    event.target.pauseVideo();
  }

  useEffect( () => {
    window.addEventListener('scroll', handleScroll);
  }, [])

  const isBottom = (el) => {
    return el.getBoundingClientRect().bottom + 120 <= window.innerHeight;
  }

  const handleScroll = (e) => {
    const wrappedElement = document.getElementById('video-trigger');
    // const stream = document.getElementById('stream');


    if(isBottom(wrappedElement)){
      setState({...state, videoWrap: true});
    }else{
      setState({...state, videoWrap: false});
    }
  }

  const handleSticky = () => {
    setState({...state, videoWrap: false, videoSticky: false});
    console.log(vidRef)
    // if(!vidRef.current.paused){
    //   vidRef.current.pause()
    // }
  }

  return (
    <div className="video">
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className={videoWrap ? 'video-wrap' : ''} >
        <div className={`video-player ` + ( videoWrap ? 'sticky': '')}>
          <svg className={ videoWrap ? 'sticky-icon-show': 'sticky-icon-hide'} onClick={handleSticky} >
            <use xlinkHref="/media/sprite.svg#icon-cross"></use>
          </svg>
          <YouTube ref={vidRef} videoId="2g811Eo7K8U" opts={opts} onReady={videoOnReady} />
          {/* <iframe ref={vidRef} id="stream" width="560" height="315" src="https://www.youtube.com/embed/06K2wXMVEd0" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen controls></iframe> */}
          {/* <video ref={vidRef} id="stream" poster="/media/image-overlay.png" controls><source src="https://www.youtube.com/watch?v=06K2wXMVEd0&ab_channel=SPMAnalyzer" type="video/mp4" frameBorder="0" gesture="media" allowFullScreen/></video> */}
        </div>
      </div>
      <div id="video-trigger"></div>
    </div>
  )
}

export default Video
