import React, {useState, useEffect, useRef} from 'react'
import('youtube.js')

// const YouTubePlayer = dynamic( () => import('youtube-player'), {ssr: false}) ;

const Video = ({}) => {

  const [state, setState] = useState({
    videoWrap: false,
    videoSticky: true
  })

  const {videoWrap, videoSticky} = state

  const vidRef = useRef(null)

  useEffect( () => {
    window.addEventListener('scroll', handleScroll);
  }, [])

  const isBottom = (el) => {
    return el.getBoundingClientRect().bottom + 120 <= window.innerHeight;
  }

  const handleScroll = (e) => {
    const wrappedElement = document.getElementById('video-trigger');
    // const stream = document.getElementById('player');
    // console.log(stream) 
    if(isBottom(wrappedElement)){
      setState({...state, videoWrap: true});
    }else{
      setState({...state, videoWrap: false});
    }
  }

  const handleSticky = () => {
    setState({...state, videoWrap: false, videoSticky: false});
    // console.log(vidRef)
    // if(!vidRef.current.paused){
    //   vidRef.current.pause()
    // }
  }

  return (
    <div className="video">
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className={videoWrap ? 'video-wrap' : ''} >
        <div className={`video-player ` + ( videoWrap ? 'sticky': '')}>
          <svg className={ videoWrap ? 'sticky-icon-show': 'sticky-icon-hide'} onClick={handleSticky} >
            <use xlinkHref="/media/sprite.svg#icon-cross"></use>
          </svg>
          <div id="player"></div>
          {/* <YouTube ref={vidRef} videoId="2g811Eo7K8U" opts={opts} onReady={videoOnReady} /> */}
          {/* <iframe ref={vidRef} id="stream" width="560" height="315" src="https://www.youtube.com/embed/06K2wXMVEd0" frameBorder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowFullScreen controls={true}></iframe> */}
          <video ref={vidRef} id="stream" x-webkit-airplay="allow" data-youtube-id="N9oxmRT2YWw" poster="/media/video/mp4" controls="true"><source src="/" frameBorder="0" gesture="media" allowFullScreen type="mp4"/></video>
        </div>
      </div>
      <div id="video-trigger"></div>
    </div>
  )
}

export default Video
