import React,{useEffect,useRef} from 'react';
import SwiperCore, { Navigation, Pagination, Scrollbar, A11y } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/components/navigation/navigation.scss';
import 'swiper/components/pagination/pagination.scss';
import 'swiper/components/scrollbar/scrollbar.scss';
import 'swiper/swiper.scss';
import 'swiper/swiper-bundle.css'



const Gallery = ({}) => {

  SwiperCore.use([Pagination, A11y]);

  return (
    <div className="gallery">
      <div className="gallery-title">Gallery</div>
      <div className="gallery-subheading">App Screenshots</div>
      <div className="gallery-slider">
      <Swiper
        spaceBetween={50}
        slidesPerView={1}
        navigation
        pagination={{ clickable: true }}
        scrollbar={{ draggable: true }}
        onSwiper={(swiper) => console.log(swiper)}
        onSlideChange={() => console.log('slide change')}
        >
        <SwiperSlide><img src="/media/gallery-1.png" alt="Person image" /></SwiperSlide>
        <SwiperSlide><img src="/media/gallery-2.png" alt="Person image" /></SwiperSlide>
        <SwiperSlide><img src="/media/gallery-3.png" alt="Person image" /></SwiperSlide>
      </Swiper>
      </div>
    </div>
  )
}

export default Gallery
