import React, {useState, useEffect, useRef} from 'react';
import dynamic from 'next/dynamic'
const Reactpip = dynamic( () => import('react-picture-in-picture'), {ssr: false}) 

const Video = ({}) => {
  // const scroll = useScroll()

  const [state, setState] = useState({
    scrollY: 0
  })

  const {scrollY} = state

  const child1 = useRef(null);

  useEffect( () => {
    console.log(child1)
    window.addEventListener('scroll', handleScroll);
  }, [])

  const handleScroll = () => {
    setState({...state, scrollY: window.scrollY});
  }

  return (
    <div className="video">
      {scrollY}
      <div className="video-title">What is SPM?</div>
      <div ref={child1} className="video-subheading">See the Video Below</div>
      <div className="video-wrap">
        <div className="video-player">
          <iframe src="/media/video.mp4" frameBorder="0" gesture="media" allowFullScreen></iframe>
        </div>
        {scrollY}
      </div>
    </div>
  )
}

export default Video
