import React, {useState, useEffect} from 'react'

const Video = ({}) => {

  const [state, setState] = useState({
    stuck: false,
    videoWrap: false
  })

  const {scrollY, stuck, videoWrap} = state

  useEffect( () => {
    window.addEventListener('scroll', handleScroll);
  }, [])

  const isBottom = (el) => {
    return el.getBoundingClientRect().bottom <= window.innerHeight;
  }

  const handleScroll = (e) => {
    const wrappedElement = document.getElementById('video-wrap');
    if(isBottom(wrappedElement)){

    }
    setState({...state, scrollY: window.scrollY});
  }

  return (
    <div className="video">
      {scrollY}
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className="video-wrap" id="video-wrap">
        <div className={`video-player ` + ( scrollY > videoWrap ? 'stuck': '')}>
          <iframe src="/media/video.mp4" frameBorder="0" gesture="media" allowFullScreen></iframe>
        </div>
        {scrollY}
      </div>
    </div>
  )
}

export default Video
