import React,{useEffect,useRef} from 'react';
import Swiper, { Navigation, Pagination } from 'swiper';
import 'swiper/swiper-bundle.min.css'
import 'swiper/swiper-bundle.min.js'
import 'swiper/swiper-bundle.css'
import 'swiper/swiper-vars.scss'
import 'swiper/swiper.scss'


const Gallery = ({}) => {

  Swiper.use([Navigation, Pagination]);

  const swiper = new Swiper();

  var mySwiper = new Swiper('.swiper-container', {
    // Optional parameters
    direction: 'horizontal',
    // loop: true,
    slidesPerView: 2,

    // If we need pagination
    pagination: {
      el: '.swiper-pagination',
      type: 'bullets',
    },

    // Navigation arrows
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
    },

    // And if we need scrollbar
    scrollbar: {
      el: '.swiper-scrollbar',
    },
  })

  return (
    <div className="gallery">
      <div className="gallery-title">Gallery</div>
      <div className="gallery-subheading">App Screenshots</div>
      <div className="gallery-slider">
      <div className="swiper-container">
          <div className="swiper-wrapper">
              <div className="swiper-slide"><img src="/media/gallery-1.png" alt="Gallery image" /></div>
              <div className="swiper-slide"><img src="/media/gallery-2.png" alt="Gallery image" /></div>
              <div className="swiper-slide"><img src="/media/gallery-3.png" alt="Gallery image" /></div>
          </div>          
          <div className="swiper-scrollbar"></div>
      </div>
      </div>
    </div>
  )
}

export default Gallery
