import React, {useState, useEffect} from 'react'

const Video = ({}) => {

  const [state, setState] = useState({
    videoWrap: false,
    videoClose: false
  })

  const {videoWrap, videoClose} = state

  useEffect( () => {
    window.addEventListener('scroll', handleScroll);
  }, [])

  const isBottom = (el) => {
    return el.getBoundingClientRect().bottom + 120 <= window.innerHeight;
  }

  const handleScroll = (e) => {
    const wrappedElement = document.getElementById('video-trigger');
    const stream = document.getElementById('stream');
    if(isBottom(wrappedElement) && !videoClose && !stream.paused){
      setState({...state, videoWrap: true});
    }

    // !stream.paused && videoWrap ? setState({...state, videoWrap: false}): null ;
  }

  const handleSticky = () => {
    console.log('Hello')
  }

  return (
    <div className="video">
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className={videoWrap ? 'video-wrap' : ''}>
        <div className={`video-player ` + ( videoWrap ? 'sticky': '')}>
          <video id="stream" controls><source src="/media/video.mp4" type="video/mp4" frameBorder="0" gesture="media" allowFullScreen onClick={handleSticky}/></video>
        </div>
      </div>
      <div id="video-trigger"></div>
    </div>
  )
}

export default Video
