import React, {useState, useEffect, useRef} from 'react';
import $ from 'jquery'

const Video = ({}) => {

  const [state, setState] = useState({
    scrollY: 0,
    stuck: false,
    videoWrap: null
  })

  const {scrollY, stuck, videoWrap} = state

  const child1 = useRef(null);

  useEffect( () => {
    console.log(window.scrollY)
    if(scrollY > 1100){
      setState({...state, videoWrap: 1457, stuck: true})
    }else{
      setState({...state, stuck: false})
    }
    // $(window).on('scroll',  function() {
    //   let windowScrollTop = $(window).scrollTop();
    //   let videoBottom = $('.video').outerHeight() + $('.video-wrap').offset().top;
    //   console.log(windowScrollTop)
    //   console.log(videoBottom)
    //   if (windowScrollTop > videoBottom) {
    //     console.log(videoBottom)
    //     $('.video-wrap').height($('.video').outerHeight());
    //     setState({...state, stuck: true})
    //   } else {
    //     $('.video-wrap').height('auto');
    //     setState({...state, stuck: false})
    //   }
    // })
    window.addEventListener('scroll', handleScroll);
  }, [])

  const handleScroll = () => {
    setState({...state, scrollY: window.scrollY});
  }

  return (
    <div className="video">
      {scrollY}
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className="video-wrap">
        <div className={`video-player ` + (stuck ? 'stuck': '')}>
          <iframe src="/media/video.mp4" frameBorder="0" gesture="media" allowFullScreen></iframe>
        </div>
        {scrollY}
      </div>
    </div>
  )
}

export default Video
