import Nav from '../components/nav'
import React, { useState } from 'react'
import { useScrollPosition } from '@n8tb1t/use-scroll-position'

const Header = ({}) => {
  const [stickOnScroll, setstickOnScroll] = useState({
    navSticky: false,
  })

  const {navSticky} = stickOnScroll

  useScrollPosition(({ prevPos, currPos }) => {
    currPos.y === 0 ? setstickOnScroll({...stickOnScroll, navSticky: false}) : setstickOnScroll({...stickOnScroll, navSticky: true});
  }, [stickOnScroll])

  return (
    <div className="main-container">
      <Nav
        sticky={navSticky}
      />
      <div className="header" style={ navSticky ? {padding: '20rem 0 0 0'} : {} }>
        <div className="header-title">SPM ANALYZER</div>
        <div className="header-subheading">
          <p>A sales rank estimator application for Amazon sellers. </p>
          <p>Download today on the iOS ang Google Play Store</p>
        </div>
        <div className="header-download">
          <a href="" className="header-download-apple"><img src="/media/apple-store-badge.png" alt="Apple Store Badge"/></a>
          <a href="" className="header-download-android"><img src="/media/google-play-badge.png" alt="Google Play Badge"/></a>
        </div>
        <img src="/media/header-iphone-half.png" alt="iPhone App Example" className="header-image"/>
      </div>
    </div>

  )
}

export default Header
