import React, {useState, useEffect, useRef} from 'react'

const Video = ({}) => {

  const [state, setState] = useState({
    scrollY: 0,
    stuck: false,
    videoWrap: null
  })

  const {scrollY, stuck, videoWrap} = state

  const child1 = useRef(null);

  useEffect( () => {
    // console.log(child1)
    // let videoOffset = child1.current.offsetTop + child1.current.scrollHeight
    // console.log(videoOffset)
    // setState({...state, videoWrap: videoOffset});
    window.addEventListener('scroll', handleScroll);
  }, [child1])

  const onScroll = () => {
    console.log(child1.current)
  }

  const handleScroll = () => {
    console.log(videoWrap)
    setState({...state, scrollY: window.scrollY});
  }
  //1320
  //1217

  return (
    <div className="video">
      {scrollY}
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className="video-wrap" ref={child1} onScroll={onScroll} >
        <div className={`video-player ` + ( scrollY > videoWrap ? 'stuck': '')}>
          <iframe src="/media/video.mp4" frameBorder="0" gesture="media" allowFullScreen></iframe>
        </div>
        {scrollY}
      </div>
    </div>
  )
}

export default Video
