import React, {useState, useEffect, useRef} from 'react';
import $ from 'jquery'

const Video = ({}) => {
  // const scroll = useScroll()

  const [state, setState] = useState({
    scrollY: 0,
    stuck: false
  })

  const {scrollY} = state

  const child1 = useRef(null);

  useEffect( () => {
    var $video = $('.video');
    console.log($video)
    // let windowScrollTop = $(window).scrollTop();
    // let videoBottom = $('.video').outerHeight() + $('.video-wrap').offset().top;

    // if (windowScrollTop > videoBottom) {
    //   $videoWrap.height(videoHeight);
    //   setState
    // } else {
    //   $videoWrap.height('auto');
    //   $video.removeClass('stuck');
    // }
    // window.addEventListener('scroll', handleScroll);
  }, [])

  const handleScroll = () => {
    setState({...state, scrollY: window.scrollY});
  }

  return (
    <div className="video">
      {scrollY}
      <div className="video-title">What is SPM?</div>
      <div className="video-subheading">See the Video Below</div>
      <div className="video-wrap">
        <div className="video-player">
          <iframe src="/media/video.mp4" frameBorder="0" gesture="media" allowFullScreen></iframe>
        </div>
        {scrollY}
      </div>
    </div>
  )
}

export default Video
